import React from 'react';
import "./menu.styles.scss";
import MenuCategory from "../menu-category/menu-category.component";

class Menu extends React.Component {
    constructor(){
        super();

        this.state = {
            categories: [
                {
                    title: 'Bugra',
                    id: 1,
                    imgURL: "https://pbs.twimg.com/profile_images/1238227706376060938/g_CsoUlg_400x400.jpg",
                },
                {
                    title: 'Petunia',
                    id: 2,
                    imgURL: "https://vignette.wikia.nocookie.net/copipastas/images/1/17/613x460x2_lulu_bittersweetskin_by_itspancake-d8a2bwe.png/revision/latest?cb=20190114142008&path-prefix=es"
                },
                {
                    title: 'Lucy',
                    id: 3,
                    imgURL: "https://vignette.wikia.nocookie.net/copipastas/images/4/4c/R5B4jvjy-0.jpg/revision/latest?cb=20190114144311&path-prefix=es"
                },
                {
                    title: 'bots',
                    id: 4,
                    size: 'large'
                },
                {
                    title: 'humanos',
                    id: 5
                }]
        };
    }

    render(){
        return (<div className="menu">
            {this.state.categories.map( ca => 
            (<MenuCategory key={ca.id} title={ca.title} imgURL={ca.imgURL}/>))
        }
        </div>);
    }
}

export default Menu;
